// obj_player → Create
hp = 3;
invulneravel = false;

// obj_player → Collision with obj_enemy
if (!invulneravel) {
    hp -= 1;
    invulneravel = true;
    var dir = point_direction(other.x, other.y, x, y);
    hspeed = lengthdir_x(6, dir);
    vspeed = lengthdir_y(6, dir);
    alarm[0] = room_speed/2; // 0.5s
}

// obj_player → Alarm 0
invulneravel = false;
