// Create (obj_player)
spd = 3;

// Step (obj_player)
var mx = keyboard_check(vk_right) - keyboard_check(vk_left);
var my = keyboard_check(vk_down) - keyboard_check(vk_up);

x += mx * spd;
y += my * spd;

// Opcional: normaliza diagonal
if (mx != 0 && my != 0) {
    x -= mx * spd * 0.2929;
    y -= my * spd * 0.2929;
}
