// obj_game → Create
global.paused = false;

// obj_game → Step
if (keyboard_check_pressed(vk_escape)) {
    global.paused = !global.paused;
}

// exemplo em obj_player → Step
if (global.paused) exit; // aborta lógica de movimento
// ... (movimento do player aqui)

// obj_game → Draw GUI
if (global.paused) {
    draw_set_halign(fa_center);
    draw_set_valign(fa_middle);
    draw_text(room_width/2, room_height/2, "PAUSADO");
}
